class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {

        int n=nums.size();
         vector<int>v(n);
        for(int i=0;i<n;i++){
            v[i]=target[i]-nums[i];
        }
        long long int ans=0;
        int a=1;
        int b = abs(v[0]);
        ans+=b;
        for(int i=1;i<n;i++){
          if(v[i]>=0 && v[i-1]>=0){
           if(v[i]>= v[i-1]){
            ans+=(v[i]-v[i-1]);
           }
          }
          else if(v[i]<0 && v[i-1]<0){
            if(v[i]<= v[i-1]){
                ans += (abs(v[i])-abs(v[i-1]));
            }
          }
          else if(v[i]>=0 && v[i-1]<0){
            ans+= v[i];
          }
          else{
            ans += abs(v[i]);
          }
        }
        return ans;

    }
};
