class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        long ops = 0;
        int prev = 0;
        for (int i = 0; i < nums.length; i++) {
            int diff = target[i] - nums[i];
            if ((diff > 0 && prev == 1) || (diff < 0 && prev == -1)) {
                if (Math.abs(diff) > Math.abs(target[i - 1] - nums[i - 1])) {
                    ops += Math.abs(diff) - Math.abs(target[i - 1] - nums[i - 1]);
                }
            } else {
                ops += Math.abs(diff);
                if (diff > 0) {
                    prev = 1;
                } else if (diff < 0) {
                    prev = -1;
                } else {
                    prev = 0;
                }
            }
        }
        return ops;

    }
}
