class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        int diff;
        int lst=0;
        long long ans=0;
        for(int i=0;i<n;i++){
            diff=target[i]-nums[i];
            // cout<<diff<<" ";
            if(diff<0){
                if(lst<0){
                    if(diff<lst)
                    ans+=abs(diff-lst);
                }
                else{
                    ans+=abs(diff);
                }
            }
            else{
                if(lst<0){
                    if(diff>lst){
                        ans+=diff;
                    }
                }
                else{
                    if(diff>lst){
                        ans+=diff-lst;
                    }
                }
            }
            // cout<<ans<<" ";
            lst=diff;
        }
        return ans;
    }
};
