int cmp(const void*a,const void*b)
{
    int x=(*(int**)a)[0];
    int y=(*(int**)b)[0];
    return abs(y)-abs(x);
}
bool same(int a,int b)
{
    if(a>0&&b>0)
        return true;
    if(a<0&&b<0)
        return true;
    return false;
}
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;
        int n=nums.size();
        int **a=(int**)malloc(sizeof(int*)*n);
        for(int i=0;i<n;i++)
        {
            a[i]=(int*)malloc(sizeof(int)*2);
            a[i][0]=target[i]-nums[i];
            a[i][1]=i;
        }
        int get[n];
        memset(get,-1,sizeof get);
        qsort(a,n,sizeof(int*),cmp);
        for(int i=0;i<n;i++)
        {
            int x=a[i][0],index=a[i][1];
            int t=abs(x);
            if(index-1>=0&&get[index-1]!=-1)
            {
                if(same(x,target[index-1]-nums[index-1]))
                    t-=abs(x);
            }
            if(index+1<n&&get[index+1]!=-1)
            {
                if(same(x,target[index+1]-nums[index+1]))
                    t-=abs(x);
            }
            get[index]=1;
            ans+=t;
        }
        return ans;
    }
};
