class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        nums = [x - y for x, y in zip(nums, target)]
        d = 0
        ans = 0
        for v in nums:
            if v < 0 and d > 0:
                if v + d < 0:
                    ans += -(v + d)
                d = -v
                continue
            elif v > 0 and d < 0:
                if v + d > 0:
                    ans += v + d
                d = -v
                continue
            ans += abs(v)
            d = -v
        return ans
