class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        d = []
        n = len(nums)
        for i in range(n):
            d.append(nums[i] - target[i])

        res = 0
        prev = 0
        for x in d:
            if x * prev > 0:
                res += max(0, abs(x) - abs(prev))
            else:
                res += abs(x)
            prev = x
        return res
