class Solution {
public:
    #define ll long long

    ll find(int i,int j,vector<int> &nums,vector<int> &target){

        vector<int> temp;

        for(int k=i;k<=j;k++){
            temp.push_back(abs(nums[k]-target[k]));
        }
        ll sz=temp.size();
        ll ans=temp[0];

        for(int k=1;k<sz;k++){
            if(temp[k]>temp[k-1]){
                ans+=(temp[k]-temp[k-1]);
            }
        }

        return ans;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;
        int maxi=0;
        int f=-1;
        int ind=0;

        int n=nums.size();

        for(int i=0;i<n;i++){
            if(nums[i]>=target[i]){
                if(f==1){
                    ans+=find(ind,i-1,nums,target);
                    ind=i;
                }
                f=0;
            }
            else{
                if(f==0){
                    ans+=find(ind,i-1,nums,target);
                    ind=i;
                }
                f=1;
            }
        }
        ans+=find(ind,n-1,nums,target);

        return ans;

    }
};
