class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector <int> diff;
        int n = nums.size();

        for(int i = 0; i < n; i ++){
            diff.push_back(target[i] - nums[i]);
            //cout << diff[i] << " ";
        }
        cout << endl;

        long long ans = abs(diff[0]);
        long long last = diff[0];
        for(int i = 1; i < n; i ++){
            if(diff[i] >= 0){
                last = max(last, (long long)0);
                //cout << last << " "<< diff[i] << endl;
                if(diff[i] > last) ans += (diff[i] - last);
                last = diff[i];
            }
            else{
                last = min(last, (long long)0);
                cout << last << " "<< diff[i] << endl;
                if(diff[i] < last) ans += (last - diff[i]);
                last = diff[i];
            }
        }

        return ans;
    }
};
