class Solution {
public:
  long long minimumOperations(vector<int>& nums, vector<int>& target) {
    for (int i = 0; i < nums.size(); i++) {
      nums[i] -= target[i];
    }
    nums.insert(nums.begin(), 0);
    nums.push_back(0);
    long long ans = 0;
    for (int i = 0; i < nums.size() - 1; i++) {
      ans += abs(nums[i] - nums[i + 1]);
    }
    return ans / 2;
  }
};
