impl Solution {
    pub fn minimum_operations(nums: Vec<i32>, target: Vec<i32>) -> i64 {
        let n = nums.len();
        let mut diff = (0..n)
            .map(|i| target[i] as i64 - nums[i] as i64)
            .collect::<Vec<i64>>();
        let mut ans = 0;
        // println!("diff={:?}", diff);
        for i in 0..n {
            if i == 0 {
                if diff[i] >= 0 {
                    ans += diff[i];
                } else {
                    ans -= diff[i];
                }
                continue;
            }
            if diff[i] >= 0 {
                if diff[i-1] >= 0 {
                    if diff[i] >= diff[i-1] {
                        ans += diff[i] - diff[i-1];
                    }
                } else {
                    ans += diff[i];
                }
            } else {
                if diff[i-1] >= 0 {
                    ans -= diff[i];
                } else {
                    if diff[i] <= diff[i-1] {
                        ans += diff[i-1] - diff[i];
                    }
                }
            }
            // println!("i={}, diff[i]={}, ans={}", i, diff[i], ans);
        }
        ans
    }
}
