class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        d = [i-j for i,j in zip(nums,target)]

        self.r = 0
        def proc(t):
            # print(t)
            pr = [0]
            for i in t:
                f = 0
                while i <= pr[-1]:
                    pr.pop()
                    f = 1
                if not f:
                    self.r += i - pr[-1]
                pr.append(i)


        # print(d)
        sign = 1
        t = []
        for i in d:
            if i > 0:
                if sign == 1:
                    t.append(i)
                else:
                    proc(t)
                    sign = 1
                    t = [i]
            elif i < 0:
                if sign == -1:
                    t.append(-i)
                else:
                    proc(t)
                    sign = -1
                    t = [-i]
            else:
                proc(t)
                sign = 1
                t = []
        if len(t):
            proc(t)


        return self.r
