class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {

        long long ans = 0, opt = 0;

        int n = nums.size();

        vector <long long> dp(n, 0);

        for(int i = 0; i < n; i++){
            dp[i] = target[i] - nums[i];

            ans += abs(dp[i]);
        }

        long long cur = dp[0];
        opt += abs(cur);

        for(int i = 1; i < n; i++){

            if(dp[i] >= 0){

                if(dp[i - 1] >= 0){

                    if(dp[i] >= cur){
                        opt += (dp[i] - cur);
                        cur = dp[i];
                    }
                    else{
                        cur = dp[i];
                    }
                }
                else{

                    opt += dp[i];
                    cur = dp[i];
                }
            }
            else{
                if(dp[i - 1] >= 0){

                    opt += abs(dp[i]);
                    cur = dp[i];
                }
                else{

                    if(dp[i] >= cur){
                        cur = dp[i];
                    }
                    else{
                        opt += abs(dp[i] - cur);
                        cur = dp[i];
                    }
                }
            }

            // opt += abs(cur);
            // cout<<i<<" "<<opt<<" || ";
        }
        // cout<<endl;
        // cout<<ans<<"\n";
        ans = min(ans, opt);

        return ans;
    }
};
