class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> diff;
        int n = nums.size();

        int large = 0;

        for(int i=0;i<n;i++){
            int dif = target[i]-nums[i];

            if(large == 0){
                large = dif;
            }
            else if(large < 0 && dif < 0){
                if(dif > large){
                    diff.push_back(large - dif);
                }
                large = dif;
            }
            else if(large > 0 && dif > 0){
                if(dif < large){
                    diff.push_back(large - dif);
                }
                large = dif;
            }
            else{
                diff.push_back(large);
                large = dif;
            }

        }
        diff.push_back(large);
        long long ans = 0;

        for(auto x:diff){
            long long val = labs(x);
            ans += val;
        }
        return ans;
    }
};
