class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int l = 0, r = 0;
        int n = nums.length;
        for (int i = 0; i < n; i++) {
            target[i] -= nums[i];
        }
        long res = Math.abs(target[0]);
        int pre = target[0];
        for (int i = 1; i < n; i++) {
            if (0 <= target[i] && pre >= 0) {
                if (target[i] <= pre) {
                    pre = target[i];
                } else {
                    res += target[i] - pre;
                    pre = target[i];
                }
            } else if (0 > target[i] && pre < 0) {
                if (target[i] >= pre) {
                    pre = target[i];
                } else {
                    res += pre - target[i];
                    pre = target[i];
                }
            } else {
                pre = target[i];
                res += Math.abs(target[i]);
            }
        }
        return res;

    }
}
