long long f(vector<int>& arr){
    long long ans = arr[0];
    for(int i=1;i<arr.size();i++){
        ans += max(arr[i]-arr[i-1],0);
    }
    return ans;
}

class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> arr;
        int n=nums.size();
        for(int i=0;i<n;i++)
            arr.push_back(target[i]-nums[i]);
        int flag=1;
        if(arr[0]<0)
            flag=-1;
        vector<int> tmp;
        long long ans = 0;
        for(int i=0;i<n;i++){
            if(flag*arr[i]>=0){
                tmp.push_back(arr[i]*flag);
            }
            else{
                ans+=f(tmp);
                tmp.clear();
                flag = -flag;
                tmp.push_back(arr[i]*flag);
            }
        }
        ans+=f(tmp);
        return ans;
    }
};
