class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        int n=nums.size();
        vector<int> diff(n);
        for(int i=0;i<n;i++){
            diff[i] = target[i]-nums[i];
        }
        bool flag = diff[0]>=0;
        vector<int> temp;
        for(int i=0;i<n;i++){
            // cout<<diff[i]<<" "<<flag<<" "<<ans<<endl;
            if((diff[i] >= 0 && !flag) || (diff[i] < 0 && flag)){
                int s = temp.size();
                long long tempans = 0;
                int pr = 0;
                for(int j=0;j<s;j++){
                    // cout<<temp[j]<< /" ";
                    if(temp[j] > pr){
                        tempans += temp[j]-pr;
                    }
                    pr = temp[j];
                }
                // cout<<endl;
                ans += tempans;
                temp.clear();
                temp.push_back(abs(diff[i]));
                flag = !flag;
            }
            else{
                temp.push_back(abs(diff[i]));
            }
        }
        int s = temp.size();
        long long tempans = 0;
        int pr = 0;
        for(int j=0;j<s;j++){
            if(temp[j] > pr){
                tempans += temp[j]-pr;
            }
            // cout<<temp[j]<<" ";
            pr = temp[j];
        }
        // cout<<endl;
        ans += tempans;
        temp.clear();
        flag = !flag;
        return ans;
    }
};
