class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff = []
        for i in range(0, len(nums)):
            diff.append(nums[i] - target[i])

        res = 0
        res += abs(diff[0])
        for i in range(1, len(nums)):
            if diff[i-1] == 0:
                res += abs(diff[i])
            else:
                if diff[i] == 0:
                    res += 0
                elif diff[i] > 0:
                    if diff[i-1] > 0:
                        if diff[i] > diff[i-1]:
                            res += diff[i] - diff[i-1]
                    elif diff[i-1] < 0:
                        res += diff[i]
                else:
                    if diff[i-1] < 0:
                        if diff[i] < diff[i-1]:
                            res += abs(diff[i] - diff[i-1])
                    elif diff[i-1] > 0:
                        res += abs(diff[i])

        return res



