class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        ans = 0
        L = []
        for a, b in zip(nums, target):
            L.append(b - a)
        cur = 0
        for num in L:
            if cur == 0:
                cur = -num
                ans += abs(cur)
            elif cur > 0:
                if num > 0:
                    cur = -num
                    ans += abs(cur)
                elif num == 0:
                    cur = 0
                else:
                    if num + cur <= 0:
                        ans += abs(num + cur)
                        cur = -num
                    else:
                        cur = -num
            elif cur < 0:
                if num < 0:
                    cur = -num
                    ans += abs(cur)
                elif num == 0:
                    cur = 0
                else:
                    if num + cur >= 0:
                        ans += abs(num + cur)
                        cur = -num
                    else:
                        cur = -num
        return ans
