class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> d1, d2, d3;
        d1.push_back(nums[0]); d2.push_back(target[0]);
        for (int i = 1; i < nums.size(); i++) {
            d1.push_back(nums[i] - nums[i - 1]);
            d2.push_back(target[i] - target[i - 1]);
        }
        for (int i = 0; i < nums.size(); i++)
            d3.push_back(d1[i] - d2[i]);
        long long pos = 0, neg = 0;
        for (int i = 0; i < nums.size(); i++) {
            if (d3[i] > 0)
                pos += d3[i];
            if (d3[i] < 0)
                neg += -d3[i];
        }
        return max(pos, neg);
    }
};
