class Solution {
    public long minimumOperations(int[] a, int[] b) {

        long last = b[0] - a[0];
        long ans = Math.abs(last);
        for (int i = 1; i < a.length; i++) {
            if (b[i] >= a[i]) {
                if (last < b[i] - a[i] && last >= 0) {
                    ans += b[i] - a[i] - last;
                    // System.out.printf("%d %d %d %d\n", a[i], b[i], last, b[i] - a[i] - last);
                } else if (last < 0) {
                    ans += b[i] - a[i];
                    // System.out.printf("%d %d %d %d\n", a[i], b[i], last, b[i] - a[i]);
                }
            } else {
                if (last >= 0) {
                    ans += a[i] - b[i];
                    // System.out.printf("%d %d %d %d\n", a[i], b[i], last, a[i] - b[i]);
                } else if (-last < a[i] - b[i]) {
                    ans += a[i] - b[i] + last;
                    // System.out.printf("%d %d %d %d\n", a[i], b[i], last, a[i] - b[i] + last);
                }
            }

            last = b[i] - a[i];

        }
        return ans;

    }
}
