class Solution {
public:
  long long minimumOperations(vector<int>& nums, vector<int>& target) {
    int n = (int)nums.size();
    vector<int> diff(n);
    for(int i = 0; i < n; i++) {
      diff[i] = target[i] - nums[i];
    }
    long long ans = 0;
    int sgn = 1;
    int cur = 0;
    for(int i = 0; i < n; i++) {
      int k = abs(diff[i]);
      if((diff[i] > 0) != (sgn > 0)) {
        ans += k;
        cur = k;
        sgn = (diff[i] > 0 ? 1 : -1);
      } else if(k > cur) {
        ans += k - cur;
      }
      cur = k;
    }
    return ans;
  }
};
