class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        int prev_diff = target[0] - nums[0];
        bool flag = (prev_diff >= 0);
        long long ret = 0;
        for (int i = 1; i < n; i++) {
            int diff = target[i] - nums[i];
            if (diff >= 0 && flag) {
                if (diff < prev_diff)
                    ret += prev_diff - diff;
                prev_diff = diff;
            } else if (diff < 0 && !flag) {
                if (diff > prev_diff)
                    ret += diff - prev_diff;
                prev_diff = diff;
            } else {
                ret += abs(prev_diff);
                prev_diff = diff;
                flag = (diff >= 0);
            }
        }       
        return ret + abs(prev_diff);
    }
};
