class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        const int n = nums.size();
        for (int i = 0; i < n; ++i) {
            target[i] -= nums[i];
        }

//         for (auto x : target) cout << x << ' ';
//         cout << endl;

        long long ans = 0;
        int last = 0;
        for (int i = 0; i < n; ++i) {
            if (last == 0) {
                ans += abs(target[i]);
                last = target[i];
            } else if (last < 0) {
                if (target[i] <= 0) {
                    ans += std::max(0, last - target[i]);
                } else {
                    ans += target[i];
                }
            } else {
                if (target[i] >= 0) {
                    ans += std::max(target[i] - last, 0);
                } else {
                    ans += abs(target[i]);
                }
            }
            last = target[i];
            // cout << i << ' ' << ans << endl;
        }
        return ans;
    }
};

