#define ll long long
class Solution {
private:
    ll minops(vector<int> & target) {
        ll ans = target[0];
        for (int i = 1; i < target.size(); ++i) {
            if (target[i] > target[i-1]) {
                ans += target[i] - target[i-1];
            }
        }
        return ans;
    }
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ll res = 0;
        int n = nums.size();
        vector<int> diff(n),neg(n,0);
        for(int i = 0 ; i < n  ;i++){
            diff[i] = target[i] - nums[i];
            if(diff[i]<0){
                neg[i] = -diff[i];
                diff[i] = 0;
            }
        }
        // cout<<sizeof(res)<<" "<<sizeof(n)<<endl;
        res+=minops(diff);
        res+=minops(neg);
        return res;
    }
};
