class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        nums = [x - y for x, y in zip(target, nums)]
        diff = [0] * n
        diff[0] = nums[0]
        pos = 0
        ng = 0
        if diff[0] > 0:
            pos += diff[0]
        else:
            ng += diff[0]
        for i in range(1, n):
            diff[i] = nums[i] - nums[i - 1]
            if diff[i] > 0:
                pos += diff[i]
            else:
                ng += diff[i]
        ans = 0
        for x in diff:
            if x > 0:
                if pos > 0:
                    r = min(x, pos)
                    ans += r
                    pos -= r
                    ng += r
                    ng = min(ng, 0)
                else:
                    pass
            elif x < 0:
                if ng < 0:
                    r = max(x, ng)
                    ans -= r
                    pos += r
                    ng -= r
                    pos = max(pos, 0)
                else:
                    pass
        return ans
