class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        for (int i = n; i --> 0;) nums[i] = target[i] - nums[i];
        long long ans = abs(nums[0]), pre = nums[0];
        for (int i = 1; i < n; i++) {
            if (nums[i] > 0) {
                if (pre < 0) {
                    ans += nums[i];
                } else {
                    ans += max(nums[i] - pre, 0ll);
                }
            } else if (nums[i] < 0) {
                if (pre > 0) {
                    ans -= nums[i];
                } else {
                    ans += max(pre - nums[i], 0ll);
                }
            }
            pre = nums[i];
        }
        return ans;
    }
};
