class Solution {
public:
    long long solve(vector<int>& A) {
        long long sum = A[0];
        for(int i = 0; i < A.size() - 1; ++i) {
            if(A[i] < A[i + 1]) {
                sum += A[i + 1] - A[i];
            }
        }
        return sum;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> r, s;
        int n = nums.size();
        long long ans = 0;
        for(int i = 0; i < n; ++i) {
            if(nums[i] > target[i]) {
                r.push_back(nums[i] - target[i]);
                if(s.size() > 0) {
                    ans += solve(s); s.clear();
                }
            } else if(nums[i] < target[i]){
                s.push_back(target[i] - nums[i]);
                if(r.size() > 0) {
                    ans += solve(r); r.clear();
                }
            } else {
                if(s.size() > 0) {
                    ans += solve(s); s.clear();
                }
                if(r.size() > 0) {
                    ans += solve(r); r.clear();
                }
            }
        }
        if(s.size() > 0) ans += solve(s);
        if(r.size() > 0) ans += solve(r);
        return ans;
    }
};
