class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long res = 0;
        int i,j;
        vector<int> diff(n,0);
        for(i=0;i<n;i++){
            diff[i] = target[i]-nums[i];
        }

        int mx = 0, mn = 0;
        for(i=0;i<n;i++){
            if(diff[i]==0) {
                // res = res+mx+abs(mn);
                mx = 0;
                mn = 0;
                continue;
            }
            else if(diff[i]>0){
                // res = res+abs(mn);
                mn = 0;
                if(diff[i]>mx){
                    res = res+ (diff[i]-mx);
                    mx = diff[i];
                }
                else{
                    mx = diff[i];
                }
                // mx = max(mx, diff[i]);
            }
            else if(diff[i]<0){
                // res = res+mx;
                mx = 0;
                if(abs(diff[i])>abs(mn)){
                    res = res+ (abs(diff[i])-abs(mn));
                    mn = diff[i];
                }
                else{
                    mn = diff[i];
                }
                // mn = min(mn, diff[i]);
            }
        }
        // res = res+mx+abs(mn);
        return res;
    }
};
