package main

import "fmt"

func minimumOperations(nums []int, target []int) int64 {
	// 1, 1, 1, 2
	// 1, -2, 2
	dec := make([]bool, len(nums))
	dp := make([]int, len(nums))
	if nums[0]-target[0] > 0 {
		dp[0] = nums[0] - target[0]
		dec[0] = true
	} else {
		dp[0] = -(nums[0] - target[0])
		dec[0] = false
	}
	for i := 1; i < len(nums); i++ {
		if nums[i-1]-target[i-1] > 0 {
			if nums[i]-target[i] > nums[i-1]-target[i-1] {
				dp[i] = dp[i-1] + nums[i] - target[i] - (nums[i-1] - target[i-1])
			} else if nums[i]-target[i] >= 0 {
				dp[i] = dp[i-1]
			} else {
				dp[i] = target[i] - nums[i] + dp[i-1]
			}
		} else {
			if (target[i] - nums[i]) > (target[i-1] - nums[i-1]) {
				dp[i] = dp[i-1] + (target[i] - nums[i]) - (target[i-1] - nums[i-1])
			} else if nums[i]-target[i] <= 0 {
				dp[i] = dp[i-1]
			} else {
				dp[i] = nums[i] - target[i] + dp[i-1]
			}
		}
	}
	return int64(dp[len(dp)-1])
}
