class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size(), pd = INT_MAX;
        long long ans = 0;
        for (int i = 0; i < n; i++)
        {
            int d = target[i] - nums[i];
            if (i == 0 || (pd > 0 && d < 0 || pd < 0 && d > 0))
                pd = 0;
            ans += max(abs(d) - abs(pd), 0);
            pd = d;
        }
        return ans;
    }
};
