class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        N = len(nums)

        # taret - nums
        dif = []
        for a,b in zip(nums,target):
            dif.append(b-a)
        print(dif)

        ans = 0
        prev = 0
        st = [0]

        for i in range(N):
            num = dif[i]
            if num > 0:
                if st[-1] < 0:
                    st = [0]
                if num >= st[-1]:
                    ans += num-st[-1]
                    st.append(num)
                else:
                    while st[-1] > num:
                        st.pop()
                    st.append(num)

            elif num < 0:
                if st[-1] > 0:
                    st = [0]
                if num <= st[-1]:
                    ans += st[-1] - num
                    st.append(num)
                else:
                    while st[-1] < num:
                        st.pop()
                    st.append(num)

            else:
                st = [0]
            # print(ans)



        return ans
