class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        const int n = nums.size();
        vector<int> pos(n), neg(n);
        int64_t ans = 0;
        int64_t cur = 0;
        for (int i = 0; i < n; i++) {
            target[i] -= nums[i];
            if (target[i] < 0) neg[i] = -target[i];
            else pos[i] = target[i];
        }
        int p1 = 0, p2 = 0;
        for (int i = 0; i < n; i++) {
            ans += max(0, pos[i] - p1);
            ans += max(0, neg[i] - p2);
            p1 = pos[i];
            p2 = neg[i];
        }
        return ans;        
    }
};
