class Solution {
         public long minimumOperations(int[] ns, int[] ts) {
        long ans = 0;
        int carry = 0;
        for (int i = 0; i < ns.length; i++) {
            if (ns[i] >= ts[i]) {
                if (carry >= 0) {
                    ans += ns[i] - ts[i];
                    carry = ts[i] - ns[i];
                } else {
                    int need = ts[i] - ns[i];
                    if (need <= carry) {
                        ans += (carry - need);
                    }
                    carry = need;
                }
            } else {
                if (carry >= 0) {
                    int need = ts[i] - ns[i];
                    if (carry >= need) {
                        carry = need;
                    } else {
                        ans += (need - carry);
                        carry = need;
                    }
                } else {
                    ans += ts[i] - ns[i];
                    carry = ts[i] - ns[i];
                }
            }
        }
        return ans;
    }
}
