class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        result = 0
        sign = 0
        height = 0
        for i,en in enumerate(nums):
            et = target[i]
            d = en - et
            if d > 0:
                if sign == 1:
                    if height < d:
                        result += d - height
                else:
                    result += d
                    sign = 1
                height = d
            elif d < 0:
                d *= -1
                if sign == -1:
                    if height < d:
                        result += d - height
                else:
                    result += d
                    sign = -1
                height = d
            else:
                height = sign = 0
        return result
