class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<long long> diff(n);

        for (int i = 0; i < n; ++i) {
            diff[i] = target[i] - nums[i];
        }

        long long maxi = abs(diff[0]);
        long long ans = 0;
        for(int i=1;i<n;i++){
            if(diff[i] == 0){
                ans += maxi;
                maxi = 0;
            }
            else if(diff[i-1]<0){
                if(diff[i]<0){
                    if(maxi>abs(diff[i])){
                        ans += maxi-abs(diff[i]);
                        maxi = abs(diff[i]);
                    }
                    else{
                        maxi = max(maxi,abs(diff[i]));
                    }
                }
                else{
                    ans += maxi;
                    maxi = abs(diff[i]);
                }
            }
            else{
                if(diff[i]>0){
                    if(maxi>abs(diff[i])){
                        ans += maxi-abs(diff[i]);
                        maxi = abs(diff[i]);
                    }
                    else{
                        maxi = max(maxi,abs(diff[i]));
                    }
                }
                else{
                    ans += maxi;
                    maxi = abs(diff[i]);
                }
            }
        }
        ans += maxi;


        return ans;
    }
};
