class Solution {
public:
    long long minimumOperations(vector<int>& v, vector<int>& t) {
        int n=t.size();
        vector<long long> d(n);
        for(int i=0;i<n;i++){
            d[i]=t[i]-v[i];
        }
        long long ans=max((long long)0,d[0]),m=d[0];
        for(int i=1;i<n;i++){
            if(d[i]>m){
                ans+=(d[i]-m);m=d[i];
            }
            else{
                m=d[i];
            }
            cout<<ans<<endl;
        }
        if(d[n-1]<0){
            ans-=d[n-1];
        }
        return ans;
    }
};
