func minimumOperations(nums []int, target []int) int64 {
    n := len(nums)
    d1 := make([]int, n+1)
    d2 := make([]int, n+1)
    cur := 0
    for i := range nums {
        d1[i] = nums[i] - cur
        cur = nums[i]
    }
    d1[n] = -cur
    cur = 0
    for i := range target {
        d2[i] = target[i] - cur
        cur = target[i]
    }
    d2[n] = -cur
    ans := 0
    p := 0
    g := 0
    for i := range nums {
        if d1[i] == d2[i] {
            continue
        }
        if d1[i] > d2[i] {
            diff := d1[i] - d2[i]
            if p >= diff {
                p -= diff
                continue
            }
            diff -= p
            p = 0
            ans += diff
            g += diff
        } else {
            diff := d2[i] - d1[i]
            if g >= diff {
                g -= diff
                continue
            }
            diff -= g
            g = 0
            ans += diff
            p += diff
        }
    }
    fmt.Println(d1, d2)
    return int64(ans)
}
