ll gcd(ll a, ll b) { return b == 0 ? a : gcd(b, a % b); }

ll lcm(ll a, ll b) { return (a * b) / gcd(a, b); }

class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ll ans(0), n(size(nums));
        vl diff(n, 0);
        for (int i = 0; i < n; i++) {
            diff[i] = target[i] - nums[i];
        }
        if (n == 1) {
            ans = abs(diff[0]);
            return ans;
        }

        ll prev = (diff[0] >= 0);
        ans = abs(diff[0]);
        for (int i = 1; i < n; i++) {
            ll temp = (diff[i] >= 0 ? 1 : 0);
            if (prev != temp) {
                ans += abs(diff[i]);
            } else {
                if (prev == 1) {
                    ans += max(0LL, diff[i] - diff[i - 1]);
                } else {
                    ans += max(0LL, diff[i - 1] - diff[i]);
                }
            }
            prev = temp;
        }
        return ans;
    }
};
