class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<long long> diff(n,0);
        for(int i = 0;i < n;i++) {
            diff[i] = target[i] - nums[i];
            //cout << diff[i] << " ";
        }
        long long ans = abs(diff[0]);
        int stat = 0;
        int lst = diff[0];
        int pos = 0;
        if(diff[0] >= 0) pos = 1;
        else pos = -1;

        for(int i = 1; i < diff.size();i++) {
            int x = diff[i];
            if(x > 0) {
                if(x > lst) {
                    ans += x - max(0,lst); //如果小于 0，只管 0
                    stat = 1;
                }else{//小于等于，其实理论上不用管，只不过趋势要变成下降
                    stat = -1;
                }
                pos = 1;//还是正的
            }else{
                if(x < lst) {
                    ans += -x - max(0,-lst); //如果大于 0，只管 0
                }
            }
            lst = x;
        }
        //cout << endl;
        return ans;
    }
};
