class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        ans = 0
        pre = 0
        for i, x in enumerate(nums):
            cur = target[i] - x
            ans += abs(cur)
            if cur * pre > 0:
                ans -= min(abs(cur), abs(pre))
            pre = cur
        return ans
