class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> v;
        for (int i = 0; i < nums.size(); ++i) {
            v.push_back(nums[i] - target[i]);
        }
        v.push_back(0);

        long long result = 0;
        int start = 0;
        bool pos = false, neg = false;
        int peak = 0;

        for (int i = 0; i < v.size(); ++i) {
            if (pos) {
                if (v[i] > 0) {
                    if (v[i] > peak) {
                        result += v[i] - peak;
                        peak = v[i];
                    } else if (v[i] < v[i - 1]) {
                        result += v[i - 1] - v[i];
                    }
                } else {
                    pos = false;
                    result -= abs(peak - v[i - 1]);
                    result += abs(v[i]);
                    peak = 0;
                    if (v[i] < 0) {
                        neg = true;
                        peak = v[i];
                    }
                }
                //cout << "pos " << result << endl;
            } else if (neg) {
                if (v[i] < 0) {
                    if (v[i] < peak) {
                        result += peak - v[i];
                        peak = v[i];
                    } else if (v[i] > v[i - 1]) {
                        result += v[i] - v[i - 1];
                    }
                } else {
                    neg = false;
                    result -= abs(peak - v[i - 1]);
                    result += abs(v[i]);
                    peak = 0;
                    if (v[i] > 0) {
                        pos = true;
                        peak = v[i];
                    }
                }
                //cout << "neg " << result << endl;
            } else {
                if (v[i] != 0) {
                    peak = v[i];
                    result += abs(v[i]);
                    if (v[i] > 0) {
                        pos = true;
                    } else {
                        neg = true;
                    }
                }
                //cout << "z " << result << endl;
            }
        }

        return result;
    }
};
