class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        const int n = (int)nums.size();
        vector<int> diff(n, 0);
        for (int i = 0; i < n; ++i) {
            diff[i] = target[i] - nums[i];
        }
        long long ans = 0;
        int last = 0;
        for (int i = 0; i < n; ++i) {
            if (diff[i] <= 0) {
                last = 0;
                continue;
            }
            if (diff[i] > last) {
                ans += (long long) (diff[i] - last);
                last = diff[i];
            } else {
                last = diff[i];
            }
        }
        last = 0;
        for (int i = 0; i < n; ++i) {
            if (diff[i] >= 0) {
                last = 0;
                continue;
            }
            if (diff[i] < last) {
                ans += (long long) (last - diff[i]);
                last = diff[i];
            } else {
                last = diff[i];
            }
        }
        return ans;
    }
};
