int f[100'001];
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        for (int i = 0; i < n; ++i) f[i] = nums[i] - target[i];
        long long res = 0, last = 0;
        for (int i = 0; i < n; ++i) {
            if (f[i] > 0) {
                if (last < 0) last = 0;
                res += max<long long>(0, f[i] - last);
            } else {
                if (last > 0) last = 0;
                res += max<long long>(0, last - f[i]);
            }
            last = f[i];
        }
        return res;
    }
};
