class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        using ll = long long;
        nums[0]-=target[0];
        ll pos=0,neg=0;
        if(nums[0]<0) neg-=nums[0];
        else pos+=nums[0];
        for(int i=1;i<nums.size();i++){
            nums[i]-=target[i];
            ll t=nums[i]-nums[i-1];
            if(t<0) neg-=t;
            else pos+=t;
        }
        ll temp=min(pos,neg);
        return pos+neg-temp;
    }
};
