class Solution {
public:
    int solve(int x1,int x2){
        if(x2 >= x1)return 0;
        return abs(x2 - x1);
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> vec;
        for(int i = 0;i < nums.size();i++){
            int d = (nums[i] - target[i]);
            vec.push_back(d);
        }
        long long ans = 0;
        for(int i = 0;i < vec.size();i++){
            if(i == 0)ans += abs(vec[i]);
            else{
                if(vec[i] > 0){
                    if(vec[i - 1] <= 0)ans += abs(vec[i]);
                    else{
                        int x1 = abs(vec[i]);
                        int x2 = abs(vec[i - 1]);
                        ans += solve(x1,x2);
                    }
                }else if(vec[i] < 0){
                    if(vec[i-1] >= 0)ans += abs(vec[i]);
                    else{
                        int x1 = abs(vec[i]);
                        int x2 = abs(vec[i - 1]);                        
                        ans += solve(x1,x2);
                    }
                }
            }
        }
        return ans;
    }
};
