class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int len = nums.length;
        for (int i = 0; i < len; ++i) {
            target[i] -= nums[i];
        }
        long ans = 0;
        int prevPos = 0;
        int prevMin = 0;
        for (int i = 0; i < len; ++i) {
            if (target[i] > 0) {
                if (target[i] > prevPos) ans += target[i] - prevPos;
                prevPos = target[i];
                prevMin = 0;
            }
            else if (target[i] < 0) {
                if (target[i] < prevMin) ans += prevMin - target[i];
                prevMin = target[i];
                prevPos = 0;
            }
            else {
                prevMin = 0;
                prevPos = 0;
            }
        }
        return ans;
    }
}
