class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<long long> cnums(n), tnums(n);
        for(int i = 0; i < n; i++) {
            if(i == 0) {
                cnums[i] = nums[i];
                tnums[i] = target[i];
            }
            else {
                cnums[i] = nums[i] - nums[i - 1];
                tnums[i] = target[i] - target[i - 1];
            }
        }
        long long ans = 0;
        // for(auto au: cnums) cout << au << ' ';
        // cout << endl;
        // for(auto au: tnums) cout << au << ' ';
        // cout << endl;
        long long cneedtoadd = 0, tneedtoadd = 0;
        for(int i = 0; i < n; i++) {
            // cmt += tnums[i] - cnums[i];
            // ans += abs(cmt);
            if(cnums[i] > tnums[i]) cneedtoadd += cnums[i] - tnums[i];
            else tneedtoadd += -cnums[i] + tnums[i];
        }
        return max(cneedtoadd, tneedtoadd);
    }
};
