class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        int prev_val = 0;
        long long ans = 0;
        for(int i = 0 ; i < n ; i++)
        {
            int diff = nums[i] - target[i];
            if((diff >= 0 && prev_val <= 0) ||(diff <= 0 && prev_val >= 0))
            {
                prev_val = diff;
                ans += (1LL * abs(diff));
            }
            else
            {
                if(abs(prev_val) >= abs(diff))
                {
                    prev_val = diff;
                }
                else
                {
                    ans += (abs(diff) - abs(prev_val))*1LL;
                    prev_val = diff;
                }
            }
        }
        return ans;

    }
};
