class Solution {
public:
    using ll = long long;
    long long minimumOperations(vector<int>& a, vector<int>& t) {
        a.insert(a.begin(), 0);
        t.insert(t.begin(), 0);
        for(int i = 1;i < t.size();i++) {
            t[i] -= a[i];
        }
        ll s[2] = {0};
        ll ans = 0;
        vector<int> b(t.size());
        for(int i = 1;i < t.size();i++) {
            b[i] = t[i] - t[i - 1];
            if(b[i] > 0) s[0] += b[i];
            else if(b[i] < 0) s[1] += -b[i];
        }
        ans += min(s[0], s[1]);
        ans += abs(s[0] - s[1]);
        return ans;
    }
};
