class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& tar) {
        int n=nums.size();
        long long ans=0;
        vector<int>cnt(n+1);
        for(int i=1;i<=n;i++)
        {
            cnt[i]=tar[i-1]-nums[i-1];
        }
        vector<long long>dp(n+1);
        for(int i=1;i<=n;i++)
        {
            if(cnt[i]>=0&&cnt[i-1]>=0)
            {
                if(cnt[i]>=cnt[i-1])
                {
                    dp[i]=dp[i-1]+cnt[i]-cnt[i-1];
                }
                else
                {
                    dp[i]=dp[i-1];
                }
            }
            else if(cnt[i]<=0&&cnt[i-1]<=0)
            {
                if(cnt[i]>cnt[i-1])
                {
                    dp[i]=dp[i-1];
                }
                else
                {
                    dp[i]=dp[i-1]+abs(cnt[i]-cnt[i-1]);
                }
            }
            else
            {
                dp[i]=dp[i-1]+abs(cnt[i]);
            }
        }
        return dp[n];
    }
};
